INSERT INTO `sc_shop_page_description` (`page_id`, `lang`, `title`, `keyword`, `description`, `content`) VALUES
('1', 'vi', 'Gii thiu', NULL, NULL, '<p><b>POCO MART&nbsp;</b>l&agrave; h thng&nbsp;si&ecirc;u th&nbsp;thuc tp o&agrave;n&nbsp;<strong>EGO</strong>, Vit Nam.&nbsp;H thng n&agrave;y khai trng ng&agrave;y th&aacute;ng 11 nm 2019. Theo thng k&ecirc; ca Vietnam Report, t&iacute;nh n th&aacute;ng 11/2020, PocoMart nm top 2 nh&agrave; b&aacute;n l c ngi ti&ecirc;u d&ugrave;ng quan t&acirc;m nht v&agrave; top 4 tr&ecirc;n bng xp hng 10 nh&agrave; b&aacute;n l uy t&iacute;n nm 2020</p>\r\n\r\n<p>Th&aacute;ng 5 nm 2021, POCO Mart c&oacute; khong 40 si&ecirc;u th v&agrave; khong hn 600 ca h&agrave;ng Pocomart&nbsp;tr&ecirc;n gn 50 tnh th&agrave;nh&nbsp;vi tng din t&iacute;ch mt bng kinh doanh hn 300.000 m<sup>2</sup>, s lng nh&acirc;n vi&ecirc;n khong hn 3.000 ngi.</p>\r\n\r\n<p>C&aacute;c sn phm ca PocoMart bao gm thc phm ti sng, thc phm s ch, qun &aacute;o, m phm,  gia dng, vn ph&ograve;ng phm,  chi, v.v.<br />\r\n<img alt=\"\" src=\"/data/page/german-poco-einrichtungsmaerkte-furniture-shops-brand-poco-market-hannover-germany-november-81372319.jpg\" /><img alt=\"\" src=\"/data/page/screenshot_1676000649.png\" /><img alt=\"\" src=\"/data/page/screenshot_1676000649.png\" /></p>'),
('987058bd-19ae-428b-b347-0cfc2bf9404d', 'vi', 'Tuyn Dng', NULL, NULL, '<p><b>M&Ocirc; T C&Ocirc;NG VIC</b><br />\r\n- T&iacute;nh tin qun l&yacute; thu chi trong ca l&agrave;m vic<br />\r\n- Tip nhn h&agrave;ng, trng b&agrave;y h&agrave;ng h&oacute;a &uacute;ng quy tc<br />\r\n- V sinh sch s si&ecirc;u th, quy thu ng&acirc;n<br />\r\n- Ch ng h tr, phc v kh&aacute;ch h&agrave;ng<br />\r\n- T vn gii &aacute;p thc mc,y&ecirc;u cu ca kh&aacute;ch h&agrave;ng khi tham quan mua sm</p>\r\n\r\n<p><b>Y&Ecirc;U CU NG VI&Ecirc;N:</b><br />\r\n- Nam/N<br />\r\n- Tt nghip THPT tr l&ecirc;n. k nng giao tip tt<br />\r\n- C&oacute; kh nng l&agrave;m vic&nbsp;<b>xoay ca (kh&ocirc;ng l&agrave;m cht ca):</b><br />\r\n+ Ca s&aacute;ng (6:00 - 14:00)<br />\r\n+ Ca chiu (13:30:00 - 22:00)<br />\r\n= &gt; ng k&yacute; ca trc l&agrave;m 1 tun</p>\r\n\r\n<p>- Chm ch, trung thc, khe mnh, nhanh nhn, h&ograve;a ng</p>\r\n\r\n<p><b>QUYN LI NH&Acirc;N VI&Ecirc;N:</b></p>\r\n\r\n<p><b>_- Lng cng th vic 4.8 triu + Thng</b>_<br />\r\n<i><b>- Lng ch&iacute;nh thc: 5,4tr (&atilde; bao gm bo him) + C&aacute;c ph&uacute;c li + C&aacute;c khon thng KPIs</b></i><br />\r\n<i>*- Tng thu nhp 6 - 9 triu + Ch  y </i>*<br />\r\n- 1 tun ngh 1 ng&agrave;y b&aacute;o qun l&iacute;<br />\r\n- c &oacute;ng y  03 loi bo him BHXH, BHYT, BHTN v&agrave; g&oacute;i bo him sc khe Bo Vit ch  cao<br />\r\n- C&oacute; c hi thng tin l&ecirc;n Ca h&agrave;ng Ph&oacute; trong v&ograve;ng 06 th&aacute;ng, ca h&agrave;ng trng sau 4 th&aacute;ng tip</p>\r\n\r\n<p>Loi h&igrave;nh c&ocirc;ng vic: To&agrave;n thi gian</p>\r\n\r\n<p>Lng: 6.000.000 - 9.000.000 mt th&aacute;ng</p>');
